// Processar dados do evento para Google Sheets
const eventData = $input.first().json;

// Dados bÃ¡sicos sempre presentes
const processedData = {
  timestamp: eventData.timestamp || new Date().toISOString(),
  event_type: eventData.event_type || 'unknown',
  challenge_name: eventData.challenge_name || '',
  challenge_title: eventData.challenge_title || '',
  challenge_level: eventData.challenge_level || '',
  user_engagement: eventData.user_engagement || '',
  session_id: eventData.sessionId || '',
  time_spent_seconds: eventData.time_spent_seconds || 0,
  user_agent: eventData.userAgent || '',
  url: eventData.url || '',

  // Dados adicionais baseados no tipo de evento
  scroll_depth: eventData.scroll_depth || 0,
  max_scroll_depth: eventData.max_scroll_depth || 0,
  milestone: eventData.milestone || 0,

  // Metadados
  processed_at: new Date().toISOString(),
  referrer: eventData.referrer || ''
};

// Log para debug
console.log('Processando evento:', processedData.event_type);
console.log('Challenge:', processedData.challenge_name);
console.log('Engagement:', processedData.user_engagement);

return processedData;

